import SearchIcon from "@mui/icons-material/Search";
import FavoriteBorderIcon from "@mui/icons-material/FavoriteBorder";
import { Fragment } from "react";
import styled from "styled-components";

const Container = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 1.5rem;
`;

const Box = styled.div`
  flex: 1 1 30rem;
  box-shadow: 0 0.5rem 1.5rem rgba(0, 0, 0, 0.1);
  border-radius: 0.5rem;
  border: 0.1rem solid rgba(0, 0, 0, 0.1);
  position: relative;
`;

const Discount = styled.img`
  height: 75%;
  z-index: 2;
`;

const Image = styled.div`
  position: relative;
  text-align: center;
  padding-top: 2rem;
  overflow: hidden;
`;

const Img = styled.div`
  height: 25rem;
  &:hover {
    transform: scale(1.1);
  }
`;

const Icons = styled.div`
  position: absolute;
  bottom: -7rem;
  left: 0;
  right: 0;
  display: flex;
  &:hover {
    bottom: 0;
  }
`;

const A = styled.div`
  height: 5rem;
  line-height: 5rem;
  font-size: 2rem;
  width: 50%;
  background: var(--orange);
  color: #fff;
  &:hover {
    background: #333;
  }
`;
const CartButton = styled.img`
  border-left: 0.1rem solid #fff7;
  border-right: 0.1rem solid #fff7;
  width: 100%;
`;

const Content = styled.img`
  padding: 2rem;
  text-align: center;
`;

const H3 = styled.img`
  font-size: 2.5rem;
  color: #333;
`;

const Price = styled.img`
  font-size: 2.5rem;
  color: var(--orange);
  font-weight: bolder;
  padding-top: 1rem;
`;

const Span = styled.img`
  font-size: 1.5rem;
  color: #999;
  font-weight: lighter;
  text-decoration: line-through;
`;
const Product = ({ item }) => {
  return (
    <Fragment>
      <Container>
        <Box>
          <Discount>{item.discount}</Discount>
          <Image>
            <Img src={item.img} alt="" />
            <Icons>
              <A>
                <FavoriteBorderIcon></FavoriteBorderIcon>
              </A>
              <A href="#">
                <CartButton>add to cart</CartButton>
              </A>
              <A>
                <SearchIcon></SearchIcon>
              </A>
            </Icons>
          </Image>
          <Content>
            <H3>{item.name}</H3>
            <Price>
              {item.price1} <Span>{item.price2} </Span>{" "}
            </Price>
          </Content>
        </Box>
      </Container>
    </Fragment>
  );
};

export default Product;
